import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import Zoom from "react-reveal/Zoom";
import { Fade } from "react-reveal";
import github from "../../images/githubg.png";
import ci from "../../images/onboarding.png";
import chat from "../../images/chatapp.png";
import pp from "../../images/portfolio.png";
import { FaCode } from "react-icons/fa";
import { Link } from "react-router-dom";
import { HiArrowRight } from "react-icons/hi";
import { CgWebsite } from "react-icons/cg";
<CgWebsite />
export default function Projects() {
  return (
    <div>
      <Container fluid className="certificate-section" id="about">
        <Container>
          <Row>
            <Col
              md={12}
              className="certificate-description d-flex justify-content-start"
            >
              <Zoom left cascade>
                <h1 className="aboutme-heading">Projects</h1>
              </Zoom>
            </Col>
            <Col md={3}>
              <Fade bottom>
                <div
                  key={1}
                  className="singleProject"
                  style={{
                    backgroundColor: "rgb(142 70 186 / 31%)",
                    border: "1px solid",
                  }}
                >
                  <div className="projectContent">
                    <h5 id={"first"} style={{ color: "#fbd9ad" }}>
                      Github Clone
                    </h5>
                    <img src={github} alt={github} />
                    <div className="project--showcaseBtn">
                      <a
                        href={
                          "https://github.com/anishmjain/github-app"
                        }
                        target="_blank"
                        rel="noreferrer"
                        className={"iconBtn"}
                        aria-labelledby={`code`}
                      >
                        <FaCode
                          id={`code`}
                          className={"icon"}
                          aria-label="Code"
                        />
                      </a>
                    </div>
                  </div>
                  <h6>
                    <p
                      className="project--desc"
                      style={{
                        background: "#fbd9ad",
                        color: "#b061df",
                        fontWeight: 600,
                      }}
                    >
                     The GitHub Clone is a web application that mirrors GitHub's interface, enabling users to search for repositories by username. It displays comprehensive details about each repository, including name, description, stars, forks, and issues.
                     </p>
                  </h6>
                  <div
                    className="project--lang"
                    style={{
                      background: "#fbd9ad",
                      color: "#b061df",
                      fontWeight: 600,
                    }}
                  >
                    HTML, CSS, JavaScript
                  </div>
                </div>
              </Fade>
            </Col>
            <Col md={3}>
              <Fade bottom>
                <div
                  key={1}
                  className="singleProject"
                  style={{
                    backgroundColor: "rgb(142 70 186 / 31%)",
                    border: "1px solid",
                  }}
                >
                  <div className="projectContent">
                    <h5 id={"first"} style={{ color: "#fbd9ad" }}>
                      Chat App 
                    </h5>
                    <img src={chat} alt={chat} />
                    <div className="project--showcaseBtn">
                      <a
                        href={
                          "https://github.com/anishmjain/we-roll"
                        }
                        target="_blank"
                        rel="noreferrer"
                        className={"iconBtn"}
                        aria-labelledby={`code`}
                      >
                        <FaCode
                          id={`code`}
                          className={"icon"}
                          aria-label="Code"
                        />
                      </a>
                    </div>
                  </div>
                  <h6>
                    <p
                      className="project--desc"
                      style={{
                        background: "#fbd9ad",
                        color: "#b061df",
                        fontWeight: 600,
                      }}
                    >
                      The Peer-to-Peer Chat Application is a real-time messaging platform designed to facilitate seamless communication between users. Built with modern web technologies, it offers an intuitive and responsive interface for real-time chatting.
                      </p>
                  </h6>
                  <div
                    className="project--lang"
                    style={{
                      background: "#fbd9ad",
                      color: "#b061df",
                      fontWeight: 600,
                    }}
                  >
                    MERN, Socket.io, Daisy UI
                  </div>
                </div>
              </Fade>
            </Col>
            <Col md={3}>
              <Fade bottom>
                <div
                  key={1}
                  className="singleProject"
                  style={{
                    backgroundColor: "rgb(142 70 186 / 31%)",
                    border: "1px solid",
                  }}
                >
                  <div className="projectContent">
                    <h5 id={"first"} style={{ color: "#fbd9ad" }}>
                      House Pricing Prediction
                    </h5>
                    <img src={ci} alt={ci} />
                    <div className="project--showcaseBtn">
                      <a
                        href={
                          "https://github.com/anishmjain/house-pricing-ml"
                        }
                        target="_blank"
                        rel="noreferrer"
                        className={"iconBtn"}
                        aria-labelledby={`code`}
                      >
                        <FaCode
                          id={`code`}
                          className={"icon"}
                          aria-label="Code"
                        />
                      </a>
                    </div>
                  </div>
                  <h6>
                    <p
                      className="project--desc"
                      style={{
                        background: "#fbd9ad",
                        color: "#b061df",
                        fontWeight: 600,
                      }}
                    >
                      Boston House Price Prediction uses machine learning to analyze the dataset, identifying patterns to predict house prices for new data. Built with Flask, it leverages various algorithms for accurate predictions.
                    </p>
                  </h6>
                  <div
                    className="project--lang"
                    style={{
                      background: "#fbd9ad",
                      color: "#b061df",
                      fontWeight: 600,
                    }}
                  >
                    Python(Numpy Pandas Matplot-lib), Flask
                  </div>
                </div>
              </Fade>
            </Col>
            <Col md={3}>
  <Fade bottom>
    <div
      key={1}
      className="singleProject"
      style={{
        backgroundColor: "rgb(142 70 186 / 31%)",
        border: "1px solid",
      }}
    >
      <div className="projectContent">
        <h5 id={"first"} style={{ color: "#fbd9ad" }}>
          Rain Prediction System
        </h5>
        <img src={pp} alt={pp} />
        <div className="project--showcaseBtn" style={{ display: "flex", gap: "10px" }}>
          <a
            href={
              ""
            }
            target="_blank"
            rel="noreferrer"
            className={"iconBtn"}
            aria-labelledby={`code`}
          >
            <FaCode
              id={`code`}
              className={"icon"}
              aria-label="Code"
            />
          </a>
          <a
            href={
              "" // Add your working website URL here
            }
            target="_blank"
            rel="noreferrer"
            className={"iconBtn"}
            aria-labelledby={`site`}
          >
            <CgWebsite
              id={`site`}
              className={"icon"}
              aria-label="Website"
            />
          </a>
        </div>
      </div>
      <h6>
        <p
          className="project--desc"
          style={{
            background: "#fbd9ad",
            color: "#b061df",
            fontWeight: 600,
          }}
        >
          Welcome to my personal portfolio. Explore my work, skills,
          and achievements in a concise and visually appealing
          format. Get a glimpse of my expertise and creativity.
        </p>
      </h6>
      <div
        className="project--lang"
        style={{
          background: "#fbd9ad",
          color: "#b061df",
          fontWeight: 600,
        }}
      >
        HTML, CSS, SCSS, React, JavaScript
      </div>
    </div>
  </Fade>
</Col>

          </Row>
